scriptlet.script:
var rpr = scriptletContext.get("RS_Previous_Response");

if(rpr === "success") {
	addMemory(vmMemorySize);
	scriptletResponse="success";
}
else if(rpr === "failure") {
	var it = scriptletContext.get("IT");
	if(it != undefined) {
		addMemory(undefined);
		scriptletContext.put("IT",null);
		scriptletResponse="success";
	}
	else {
		it = 1;
		scriptletContext.put("IT",it);
		scriptletResponse="sleep";
	}
}
else if(rpr === "no more") {
	addMemory(undefined);
	scriptletContext.put("IT",null);
	scriptletResponse="success";
}

function addMemory(value) {
	var tmp = scriptletContext.get("tmp");
	if(tmp == undefined) {	
		tmp = {};
		tmp['vmid'] = vmid;
		tmp['vmname'] = vmname;
	}
	else {
		tmp = JSON.parse(tmp);
	}
	if(value != undefined && value.match(/^[0-9]+$/)) {
		tmp['vmMemorySize'] = value;
	}
	scriptletContext.put("tmp",JSON.stringify(tmp));

}

var rpr = scriptletContext.get("RS_Previous_Response");

if(rpr === "success") {
	addCPU(vmCpuCount);
	scriptletResponse="success";
}
else if(rpr === "failure") {
	var it = scriptletContext.get("IT");
	if(it != undefined) {
		addCPU(undefined);
		scriptletContext.put("IT",null);
		scriptletResponse="success";
	}
	else {
		it = 1;
		scriptletContext.put("IT",it);
		scriptletResponse="sleep";
	}
}
else if(rpr === "no more") {
	addCPU(undefined);
	scriptletContext.put("IT",null);
	scriptletResponse="success";
}

function addCPU(value) {
	var tmp = scriptletContext.get("tmp");
	if(tmp == undefined) {	
		tmp = {};
		tmp['vmid'] = vmid;
		tmp['vmname'] = vmname;
	}
	else {
		tmp = JSON.parse(tmp);
	}
	if(value != undefined && value.match(/^[0-9]+$/)) {
		tmp['vmCpuCount'] = value;
	}
	scriptletContext.put("tmp",JSON.stringify(tmp));
}

try {
	var capacity = scriptletContext.get("capacity");
	var tmp = scriptletContext.get("tmp");
	var pool;
	if(capacity != undefined && capacity.trim() != "") {
		capacity = JSON.parse(capacity);
		if(capacity.hasOwnProperty(poolName)) {
			pool = capacity[poolName];		
		}
		else {
			pool = [];
		}
	}
	else {
		capacity = {};
		pool = [];	
	}

	if(tmp != undefined) {
		tmp = JSON.parse(tmp);
		pool.push(tmp);
		capacity[poolName] = pool;
	}
	else {
		capacity[poolName] = pool;
	}

	scriptletContext.putGlobal("capacity",JSON.stringify(capacity));
	scriptletContext.put("tmp", null);
	scriptletResponse="success";
}
catch(Error) {
	scriptletResponse="failure";
	scriptletContext.put("FailureMessage",Error);
}
